import React, { useState } from "react"
import { View, Text, Pressable, TextInput } from "react-native"

export function InfoBadge({task = "Buy Coffee" } :{task : string}) {
    const [save, setSave] = useState(true)
    const [time, setTime] = useState(4)


    function onSave() {


        setInterval(() => {

            // setTime((el) =>  el - 1)

            if (time == 0) {

                setTime(4)
            }
        })


    }

    return (
        <View className=" w-60   bg-neutral-400  pb-0 rounded-md ">
            <View className="pt-4 px-4">
                <Text className="  font-medium flex gap-3 items-center"> {save ? <CircleArrowRight size={18} /> : <CircleArrowRight size={18} fill='green' />} {save ? 'save your changes' : 'changes saves'}  </Text>
                <View className="">
                    <Text className="py-1 font-medium text-neutral-700">{save ? "Selected Tasks" : "Task has been updated"}</Text>
                    {

                        save &&
                        <View>
                            <Pressable className="bg-neutral-200 py-1 px-3 rounded-md"  >
                                <Text className=" font-medium" >
                                    {
                                        task
                                    }
                                </Text>
                            </Pressable>
                        </View>
                    }

                    <Pressable className="bg-slate-600 w-fit py-2 px-3 rounded-md my-2" onPress={() => { setSave((prev) => !prev); onSave() }}>
                        <Text className="font-medium text-white">
                            {
                                save ? "Save" : "Undo"
                            }
                        </Text>
                    </Pressable>
                </View>
            </View>

            {save ? '' : <View className="bg-neutral-200 h-8 "> <Text className="w-full m-auto px-4">This Message close in {time} seconds</Text> </View>}

        </View>
    )
}

