import React, { useState } from "react";
import { Pressable, View, Text } from "react-native";
import { cn } from "../../utils/cn";

import Animated , * as Animate from 'react-native-reanimated'
import { FilterIcon  } from "lucide-react-native";


type FilterButtons = {
    icon?: React.ReactNode;
    label: string;
}

type AddFilters = {
    icon?: React.ReactNode;
    label: string

}

interface FilterProps {
    filterButtons: FilterButtons[];
    addFilters: AddFilters[]
}


export function Filter({ filterButtons, addFilters }: FilterProps) {
    const [filter, setFilter] = useState(false)


    return (
        <View className="h-36 w-80 shadow-md rounded-xl m-10 ">
            < Text className={cn("p-4 font-bold flex gap-2 items-center")}>
                Applied Filters <FilterIcon size={15} fill='black'/>
            </Text>


            <View className="grid grid-cols-[1fr_1fr_1fr] gap-2 m-2">
                {
                    filterButtons.map((el, id) => (
                        <FilterButton key={id} label={el.label} icon={el.icon} />
                    ))
                }

                <View className="">
                    <Pressable onPress={() => setFilter((prev) => !prev)} className={cn(' p-2 size-8 flex items-center justify-center rounded-md',
                        filter ? "bg-red-300" : "bg-green-300"
                    )}>
                        {
                            addFilters.length > 0 ? filter ? "x" : '+' : ''
                        }
                    </Pressable>

                    {addFilters.length > 0 && filter && <View className="shadow-md bg-white min-w-28 absolute left-10 top-2 rounded-md p-1">
                        {
                            addFilters.map((el, id) => (
                                <AddFilter label={el.label} />
                            ))
                        }


                    </View>}
                </View>
            </View>






        </View>
    )
}


